package com.example.myapplication

import android.content.ContentValues
import android.content.Intent
import android.os.Bundle
import android.view.ContextMenu
import android.view.MenuItem
import android.view.View
import android.widget.*
import androidx.appcompat.app.AppCompatActivity

class MainActivity : AppCompatActivity() {
    lateinit var dbHelper: DBHelper
    lateinit var listView: ListView
    lateinit var listData: ArrayList<String>
    lateinit var adapter: ArrayAdapter<String>
    var selectedItem: String? = null

    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)
        setContentView(R.layout.activity_main)

        dbHelper = DBHelper(this)

        val cbRice = findViewById<CheckBox>(R.id.cbRice)
        val cbMilk = findViewById<CheckBox>(R.id.cbMilk)
        val cbBread = findViewById<CheckBox>(R.id.cbBread)

        val rbSmall = findViewById<RadioButton>(R.id.rbSmall)
        val rbMedium = findViewById<RadioButton>(R.id.rbMedium)
        val rbLarge = findViewById<RadioButton>(R.id.rbLarge)

        val btnAdd = findViewById<Button>(R.id.btnAdd)
        val btnShow = findViewById<Button>(R.id.btnShow)

        listView = findViewById(R.id.listView)
        listData = dbHelper.getAllItems()
        adapter = ArrayAdapter(this, android.R.layout.simple_list_item_1, listData)
        listView.adapter = adapter
        registerForContextMenu(listView)

        btnAdd.setOnClickListener {
            val selectedItems = mutableListOf<String>()
            if (cbRice.isChecked) selectedItems.add("Rice")
            if (cbMilk.isChecked) selectedItems.add("Milk")
            if (cbBread.isChecked) selectedItems.add("Bread")

            val size = when {
                rbSmall.isChecked -> "Small"
                rbMedium.isChecked -> "Medium"
                rbLarge.isChecked -> "Large"
                else -> "Unknown"
            }

            for (item in selectedItems) {
                dbHelper.insertItem("$item - $size")
            }

            refreshList()
        }

        btnShow.setOnClickListener {
            val intent = Intent(this, GridActivity::class.java)
            startActivity(intent)
        }

        listView.setOnItemLongClickListener { _, _, position, _ ->
            selectedItem = listData[position]
            false
        }
    }

    override fun onCreateContextMenu(menu: ContextMenu?, v: View?, menuInfo: ContextMenu.ContextMenuInfo?) {
        super.onCreateContextMenu(menu, v, menuInfo)
        menu?.add("Update")
        menu?.add("Delete")
    }

    override fun onContextItemSelected(item: MenuItem): Boolean {
        when (item.title) {
            "Update" -> {
                selectedItem?.let {
                    dbHelper.updateItem(it, "$it (Updated)")
                }
            }
            "Delete" -> {
                selectedItem?.let {
                    dbHelper.deleteItem(it)
                }
            }
        }
        refreshList()
        return true
    }

    private fun refreshList() {
        listData.clear()
        listData.addAll(dbHelper.getAllItems())
        adapter.notifyDataSetChanged()
    }
}

package com.example.myapplication

import android.content.Context
import android.database.sqlite.SQLiteDatabase
import android.database.sqlite.SQLiteOpenHelper
import android.content.ContentValues

class DBHelper(context: Context) : SQLiteOpenHelper(context, "GroceryDB", null, 1) {
    override fun onCreate(db: SQLiteDatabase) {
        db.execSQL("CREATE TABLE grocery(item TEXT PRIMARY KEY)")
    }

    override fun onUpgrade(db: SQLiteDatabase, oldVersion: Int, newVersion: Int) {
        db.execSQL("DROP TABLE IF EXISTS grocery")
        onCreate(db)
    }

    fun insertItem(item: String) {
        val db = writableDatabase
        val values = ContentValues().apply {
            put("item", item)
        }
        db.insert("grocery", null, values)
    }

    fun getAllItems(): ArrayList<String> {
        val list = ArrayList<String>()
        val db = readableDatabase
        val cursor = db.rawQuery("SELECT * FROM grocery", null)
        while (cursor.moveToNext()) {
            list.add(cursor.getString(0))
        }
        cursor.close()
        return list
    }

    fun updateItem(oldItem: String, newItem: String) {
        val db = writableDatabase
        val values = ContentValues().apply {
            put("item", newItem)
        }
        db.update("grocery", values, "item=?", arrayOf(oldItem))
    }

    fun deleteItem(item: String) {
        val db = writableDatabase
        db.delete("grocery", "item=?", arrayOf(item))
    }
}
