package com.example.myapplication

import android.content.ContentValues
import android.content.Context
import android.database.sqlite.SQLiteDatabase
import android.database.sqlite.SQLiteOpenHelper

class DatabaseHelper(context: Context) : SQLiteOpenHelper(context, "UserDB", null, 1) {
    override fun onCreate(db: SQLiteDatabase) {
        db.execSQL("CREATE TABLE users(name TEXT PRIMARY KEY, password TEXT, grade TEXT)")
    }

    override fun onUpgrade(db: SQLiteDatabase, oldVersion: Int, newVersion: Int) {
        db.execSQL("DROP TABLE IF EXISTS users")
        onCreate(db)
    }

    fun registerUser(name: String, password: String): Boolean {
        val db = writableDatabase
        val contentValues = ContentValues().apply {
            put("name", name)
            put("password", password)
            put("grade", "")
        }
        val result = db.insert("users", null, contentValues)
        return result != -1L
    }

    fun checkUser(name: String, password: String): Boolean {
        val db = readableDatabase
        val cursor = db.rawQuery("SELECT * FROM users WHERE name=? AND password=?", arrayOf(name, password))
        val exists = cursor.count > 0
        cursor.close()
        return exists
    }

    fun updateGrade(name: String, grade: String): Boolean {
        val db = writableDatabase
        val contentValues = ContentValues().apply {
            put("grade", grade)
        }
        val result = db.update("users", contentValues, "name=?", arrayOf(name))
        return result > 0
    }
}

package com.example.myapplication

import android.content.Intent
import android.os.Bundle
import android.widget.*
import androidx.appcompat.app.AppCompatActivity

class MainActivity : AppCompatActivity() {
    private lateinit var nameEditText: EditText
    private lateinit var passwordEditText: EditText
    private lateinit var loginButton: Button
    private lateinit var registerButton: Button
    private lateinit var db: DatabaseHelper

    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)
        setContentView(R.layout.activity_main)

        nameEditText = findViewById(R.id.editTextName)
        passwordEditText = findViewById(R.id.editTextPassword)
        loginButton = findViewById(R.id.buttonLogin)
        registerButton = findViewById(R.id.buttonRegister)

        db = DatabaseHelper(this)

        registerButton.setOnClickListener {
            val name = nameEditText.text.toString()
            val password = passwordEditText.text.toString()
            if (db.registerUser(name, password)) {
                Toast.makeText(this, "User registered!", Toast.LENGTH_SHORT).show()
            } else {
                Toast.makeText(this, "Registration failed!", Toast.LENGTH_SHORT).show()
            }
        }

        loginButton.setOnClickListener {
            val name = nameEditText.text.toString()
            val password = passwordEditText.text.toString()
            if (db.checkUser(name, password)) {
                val intent = Intent(this, GradeEntryActivity::class.java)
                intent.putExtra("name", name)
                startActivity(intent)
            } else {
                Toast.makeText(this, "Invalid credentials", Toast.LENGTH_SHORT).show()
            }
        }
    }
}

package com.example.myapplication

import android.os.Bundle
import android.widget.*
import androidx.appcompat.app.AppCompatActivity

class GradeEntryActivity : AppCompatActivity() {
    private lateinit var gradeEditText: EditText
    private lateinit var submitButton: Button
    private lateinit var db: DatabaseHelper
    private lateinit var name: String

    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)
        setContentView(R.layout.activity_grade_entry)

        gradeEditText = findViewById(R.id.editTextGrade)
        submitButton = findViewById(R.id.buttonSubmitGrade)

        name = intent.getStringExtra("name") ?: ""
        db = DatabaseHelper(this)

        submitButton.setOnClickListener {
            val grade = gradeEditText.text.toString()
            if (db.updateGrade(name, grade)) {
                Toast.makeText(this, "Grade updated!", Toast.LENGTH_SHORT).show()
            } else {
                Toast.makeText(this, "Grade update failed!", Toast.LENGTH_SHORT).show()
            }
        }
    }
}
