package com.example.myapplication;

import android.os.Bundle;
import androidx.appcompat.app.AppCompatActivity;
import androidx.viewpager2.widget.ViewPager2;
import com.google.android.material.tabs.TabLayout;
import com.google.android.material.tabs.TabLayoutMediator;

public class MainActivity extends AppCompatActivity {

    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_main); // Set the layout for the activity

        // Initialize the TabLayout from the layout
        TabLayout tabLayout = findViewById(R.id.tabLayout);

        // Initialize the ViewPager2 from the layout
        ViewPager2 viewPager = findViewById(R.id.viewPager);

        // Create a new adapter for the ViewPager2
        NewsPagerAdapter adapter = new NewsPagerAdapter(this);

        // Set the adapter for the ViewPager2 to manage the page views
        viewPager.setAdapter(adapter);

        // Set up the TabLayout with the ViewPager2
        new TabLayoutMediator(tabLayout, viewPager, (tab, position) -> { //acts as an iterator
            // Customize the tab text based on the current position
            switch (position) {
                case 0:
                    tab.setText("
Top Stories"); // Tab for the first page (Top Stories)
                    break;
                case 1:
                    tab.setText("
Sports"); // Tab for the second page (Sports)
                    break;
                case 2:
                    tab.setText("
Entertainment"); // Tab for the third page (Entertainment)
                    break;
            }
        }).attach(); // Attach the TabLayoutMediator to link the TabLayout and ViewPager
    }
}

package com.example.myapplication;

import android.os.Bundle;
import android.view.LayoutInflater;
import android.view.View;
import android.view.ViewGroup;
import android.widget.TextView;
import androidx.fragment.app.Fragment;

public class NewsFragment extends Fragment {

    private String category; // Store the category passed to the fragment (e.g., Top Stories, Sports, etc.)

    // Constructor that accepts the category name to be displayed
    public NewsFragment(String category) {
        this.category = category;
    }

    @Override
    public View onCreateView(LayoutInflater inflater, ViewGroup container, Bundle savedInstanceState) {
        // Inflate the layout for this fragment (fragment_news.xml)
        View view = inflater.inflate(R.layout.fragment_news, container, false);
        //creating a view from fragment_news.xml and changing it's content and heading

        // Find the TextView for displaying the category title
        TextView categoryTitle = view.findViewById(R.id.categoryTitle);
        // Set the category title dynamically based on the passed category string
        categoryTitle.setText(category);

        // Create some dummy news content for the selected category
        TextView newsContent = view.findViewById(R.id.newsContent);
        String dummyNews = "1. News item about " + category + "
2. Another news item about " + category + "
3. More news on " + category;

        // Set the dummy news content in the TextView
        newsContent.setText(dummyNews);

        // Return the inflated view, which is now populated with category and dummy news content
        return view;
    }
}

package com.example.myapplication;

import android.os.Bundle;
import androidx.annotation.NonNull;
import androidx.fragment.app.Fragment;
import androidx.viewpager2.adapter.FragmentStateAdapter;

public class NewsPagerAdapter extends FragmentStateAdapter {

    // Constructor: The adapter is tied to the MainActivity (which hosts the ViewPager)
    public NewsPagerAdapter(@NonNull MainActivity fragmentActivity) {
        super(fragmentActivity);
    }

    // This method is called to create a new fragment based on the position (tab) in the ViewPager
    @NonNull
    @Override
    public Fragment createFragment(int position) {
        // Create a new fragment based on the position (tab)
        Fragment fragment;
        switch (position) {
            case 0:
                // First tab (Top Stories)
                fragment = new NewsFragment("Top Stories");
                break;
          case 1:
                // Second tab (Sports)
                fragment = new NewsFragment("Sports");
                break;
            case 2:
                // Third tab (Entertainment)
                fragment = new NewsFragment("Entertainment");
                break;
            default:
                // Default case to avoid errors (set to Top Stories if no matching tab is found)
                fragment = new NewsFragment("Top Stories");
        }
        return fragment; // Return the created fragment for this tab
    }

    // This method returns the number of items (tabs) in the ViewPager
    @Override
    public int getItemCount() {
        return 3; // Three tabs: Top Stories, Sports, and Entertainment
    }
}
