package com.example.myapplication;

import android.os.Bundle;
import androidx.appcompat.app.AppCompatActivity;
import androidx.viewpager2.widget.ViewPager2;
import com.google.android.material.tabs.TabLayout;
import com.google.android.material.tabs.TabLayoutMediator;

public class MainActivity extends AppCompatActivity {

    // onCreate is called when the activity is first created
    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_main);  // Set the layout for the activity

        // Find the TabLayout and ViewPager2 from the layout
        TabLayout tabLayout = findViewById(R.id.tabLayout);
        ViewPager2 viewPager = findViewById(R.id.viewPager);

        // Initialize the adapter for ViewPager2 which will handle fragments
        ViewPagerAdapter adapter = new ViewPagerAdapter(this);
        viewPager.setAdapter(adapter);  // Set the adapter to the ViewPager2

        // Link the TabLayout with ViewPager2 and set the tab titles dynamically
        new TabLayoutMediator(tabLayout, viewPager,
                (tab, position) -> {  // Set the tab titles based on the position
                    switch (position) {
                        case 0: tab.setText("Artists"); break;  // Tab for Artists
                        case 1: tab.setText("Albums"); break;   // Tab for Albums
                        case 2: tab.setText("Songs"); break;    // Tab for Songs
                    }
                }).attach();  // Attach the mediator to link ViewPager and TabLayout
    }
}

package com.example.myapplication;

import android.os.Bundle;
import androidx.annotation.NonNull;
import androidx.annotation.Nullable;
import androidx.fragment.app.Fragment;
import android.view.LayoutInflater;
import android.view.View;
import android.view.ViewGroup;
import android.widget.ArrayAdapter;
import android.widget.ListView;
import java.util.Arrays;
import java.util.List;

public class AlbumsFragment extends Fragment {

    // onCreateView is called when it's time to create the view for this fragment
    @Nullable
    @Override
    public View onCreateView(@NonNull LayoutInflater inflater, @Nullable ViewGroup container, @Nullable Bundle savedInstanceState) {
        // Inflate the layout for this fragment, fragment_list.xml, which contains the ListView
        View view = inflater.inflate(R.layout.fragment_list, container, false);

        // Find the ListView in the layout
        ListView listView = view.findViewById(R.id.listView);

        // Create a list of album names
        List<String> albums = Arrays.asList("Recovery", "Certified Lover Boy", "After Hours", "1989", "Happier Than Ever");

        // Create an ArrayAdapter to bind the list of albums to the ListView
        // android.R.layout.simple_list_item_1 is a built-in layout that displays a single text item
        ArrayAdapter<String> adapter = new ArrayAdapter<>(requireContext(), android.R.layout.simple_list_item_1, albums);

        // Set the adapter to the ListView so the data will be displayed
        listView.setAdapter(adapter);

        // Return the view to display
        return view;
    }
}

package com.example.myapplication;

import android.content.Context;
import androidx.annotation.NonNull;
import androidx.fragment.app.Fragment;
import androidx.fragment.app.FragmentActivity;
import androidx.viewpager2.adapter.FragmentStateAdapter;

public class ViewPagerAdapter extends FragmentStateAdapter {

    // Constructor that initializes the adapter with the FragmentActivity
    public ViewPagerAdapter(@NonNull FragmentActivity fragmentActivity) {
        super(fragmentActivity);
    }

    // This method is called to create a fragment for a specific position
    @NonNull
    @Override
    public Fragment createFragment(int position) {
        // Switch case to return the appropriate fragment based on the position
        switch (position) {
            case 0: // If the position is 0, return the ArtistsFragment
                return new ArtistsFragment();
            case 1: // If the position is 1, return the AlbumsFragment
                return new AlbumsFragment();
            case 2: // If the position is 2, return the SongsFragment
                return new SongsFragment();
            default: // In case of an unexpected position, return the ArtistsFragment as default
                return new ArtistsFragment();
        }
    }

    // This method returns the total number of fragments (in this case, 3)
    @Override
    public int getItemCount() {
        return 3; // The number of tabs (Artists, Albums, Songs)
    }
}
