package com.example.endsem;

import android.app.DatePickerDialog;
import android.content.*;
import android.database.Cursor;
import android.os.Bundle;
import android.view.View;
import android.widget.*;
import androidx.appcompat.app.AppCompatActivity;

import java.util.Calendar;

public class MainActivity extends AppCompatActivity {

    Spinner spinner;
    TextView dateText, durationText;
    SeekBar seekBar;
    Button pickDateBtn, submitBtn, viewTripsBtn, logoutBtn;

    DatabaseHelper db;
    SharedPreferences prefs;
    String username;

    @Override
    protected void onCreate(Bundle b) {
        super.onCreate(b);
        setContentView(R.layout.activity_main);

        db = new DatabaseHelper(this);
        prefs = getSharedPreferences("prefs", MODE_PRIVATE);
        username = prefs.getString("logged_user", "");

        spinner = findViewById(R.id.spinner);
        dateText = findViewById(R.id.dateText);
        durationText = findViewById(R.id.durationText);
        seekBar = findViewById(R.id.seekBar);
        pickDateBtn = findViewById(R.id.pickDate);
        submitBtn = findViewById(R.id.submitBtn);
        viewTripsBtn = findViewById(R.id.viewTrips);
        logoutBtn = findViewById(R.id.logoutBtn);

        ArrayAdapter<CharSequence> adapter = ArrayAdapter.createFromResource(this, R.array.destinations, android.R.layout.simple_spinner_item);
        adapter.setDropDownViewResource(android.R.layout.simple_spinner_dropdown_item);
        spinner.setAdapter(adapter);

        // Load saved spinner choice
        spinner.setSelection(prefs.getInt("spinner_index", 0));

        seekBar.setOnSeekBarChangeListener(new SeekBar.OnSeekBarChangeListener() {
            public void onProgressChanged(SeekBar s, int p, boolean f) {
                durationText.setText(p + " Days");
            }

            public void onStartTrackingTouch(SeekBar seekBar) {}
            public void onStopTrackingTouch(SeekBar seekBar) {}
        });

        pickDateBtn.setOnClickListener(v -> {
            Calendar c = Calendar.getInstance();
            DatePickerDialog d = new DatePickerDialog(this, (view, year, month, day) -> {
                dateText.setText(day + "/" + (month+1) + "/" + year);
            }, c.get(Calendar.YEAR), c.get(Calendar.MONTH), c.get(Calendar.DAY_OF_MONTH));
            d.show();
        });

        submitBtn.setOnClickListener(v -> {
            String dest = spinner.getSelectedItem().toString();
            String date = dateText.getText().toString();
            int duration = seekBar.getProgress();

            db.addTrip(username, dest, date, duration);
            prefs.edit().putInt("spinner_index", spinner.getSelectedItemPosition()).apply();
            Toast.makeText(this, "Trip Saved!", Toast.LENGTH_SHORT).show();
        });

        viewTripsBtn.setOnClickListener(v -> {
            startActivity(new Intent(this, TripListActivity.class));
        });

        logoutBtn.setOnClickListener(v -> {
            prefs.edit().remove("logged_user").apply();
            startActivity(new Intent(this, LoginActivity.class));
            finish();
        });
    }
}






