package com.example.practice1;

import android.content.Intent;
import android.database.Cursor;
import android.database.sqlite.SQLiteDatabase;
import android.os.Bundle;
import android.view.ContextMenu;
import android.view.MenuInflater;
import android.view.MenuItem;
import android.view.View;
import android.widget.AdapterView;
import android.widget.ArrayAdapter;
import android.widget.Button;
import android.widget.ListView;
import android.widget.PopupMenu;
import android.widget.Toast;

import androidx.appcompat.app.AppCompatActivity;

import java.util.ArrayList;
import java.util.List;

public class ReadReviewActivity extends AppCompatActivity {

    private TaskDatabaseHelper dbHelper; // Database helper
    private ListView reviewListView; // ListView to display reviews
    private List<String> reviewsList; // Store reviews for display
    private List<Integer> reviewIds; // Store IDs of reviews for actions
    private Button filterBtn1; // Filter button

    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_review_list);

        // Initialize database helper
        dbHelper = new TaskDatabaseHelper(this);

        // Initialize views
        reviewListView = findViewById(R.id.listView);
        filterBtn1 = findViewById(R.id.filterBtn);

        // Load and display all reviews
        loadReviews(null, null, "id DESC");

        // Register ListView for context menu
        registerForContextMenu(reviewListView);

        // Set up filter button with PopupMenu
        filterBtn1.setOnClickListener(view -> {
            PopupMenu popupMenu = new PopupMenu(ReadReviewActivity.this, view);
            MenuInflater inflater = popupMenu.getMenuInflater();
            inflater.inflate(R.menu.popup_menu, popupMenu.getMenu());

            popupMenu.setOnMenuItemClickListener(item -> {
                if (item.getItemId() == R.id.option_one) {
                    Toast.makeText(ReadReviewActivity.this, "Sorting by Rating...", Toast.LENGTH_SHORT).show();
                    loadReviews(null, null, "CAST(points AS INTEGER) DESC"); // Sort by rating descending
                    return true;
                } else if (item.getItemId() == R.id.option_two) {
                    Toast.makeText(ReadReviewActivity.this, "Filtering by Genre: Comedy...", Toast.LENGTH_SHORT).show();
                    loadReviews("genre=?", new String[]{"Comedy"}, "id DESC"); // Filter by Comedy genre
                    return true;
                } else {
                    return false;
                }
            });

            popupMenu.show(); // Show popup menu
        });
    }

    private void loadReviews(String selection, String[] selectionArgs, String orderBy) {
        reviewsList = new ArrayList<>();
        reviewIds = new ArrayList<>();
        SQLiteDatabase db = dbHelper.getReadableDatabase();

        try {
            Cursor cursor = db.query(
                    "Reviews",   // Table name
                    null,        // All columns
                    selection,   // WHERE clause (filter)
                    selectionArgs,  // WHERE arguments (filter values)
                    null,        // No GROUP BY
                    null,        // No HAVING
                    orderBy      // Order by clause (e.g., sort by rating or ID)
            );

            if (cursor.getCount() == 0) {
                reviewsList.add("No reviews found");
            } else {
                while (cursor.moveToNext()) {
                    int id = cursor.getInt(cursor.getColumnIndexOrThrow("id"));
                    String name = cursor.getString(cursor.getColumnIndexOrThrow("name"));
                    String year = cursor.getString(cursor.getColumnIndexOrThrow("year"));
                    String points = cursor.getString(cursor.getColumnIndexOrThrow("points"));
                    String genre = cursor.getString(cursor.getColumnIndexOrThrow("genre"));

                    String reviewEntry = String.format("%s (%s) - %s/5 - %s", name, year, points, genre);
                    reviewsList.add(reviewEntry);
                    reviewIds.add(id); // Store the ID for later actions
                }
            }

            cursor.close();
        } catch (Exception e) {
            reviewsList.add("Error loading reviews: " + e.getMessage());
        } finally {
            db.close();
        }

        ArrayAdapter<String> adapter = new ArrayAdapter<>(this,
                android.R.layout.simple_list_item_1, reviewsList);
        reviewListView.setAdapter(adapter);
    }

    @Override
    public void onCreateContextMenu(ContextMenu menu, View v, ContextMenu.ContextMenuInfo menuInfo) {
        super.onCreateContextMenu(menu, v, menuInfo);

        if (v.getId() == R.id.listView) { // Ensure it's the ListView triggering this menu
            getMenuInflater().inflate(R.menu.contextual_action_menu, menu); // Inflate menu options
        }
    }

    @Override
    public boolean onContextItemSelected(MenuItem item) {
        AdapterView.AdapterContextMenuInfo info = (AdapterView.AdapterContextMenuInfo) item.getMenuInfo();
        int position = info.position; // Get position of selected item

        int selectedReviewId = reviewIds.get(position); // Get corresponding review ID

        if (item.getItemId() == R.id.action_edit) {
            SQLiteDatabase db = dbHelper.getReadableDatabase();
            Cursor cursor = db.query(
                    "Reviews",
                    null,
                    "id=?",
                    new String[]{String.valueOf(selectedReviewId)},
                    null,
                    null,
                    null
            );

            if (cursor.moveToFirst()) {
                String name = cursor.getString(cursor.getColumnIndexOrThrow("name"));
                String year = cursor.getString(cursor.getColumnIndexOrThrow("year"));
                String points = cursor.getString(cursor.getColumnIndexOrThrow("points"));
                String review = cursor.getString(cursor.getColumnIndexOrThrow("review"));
                String genre = cursor.getString(cursor.getColumnIndexOrThrow("genre"));

                Intent intent = new Intent(ReadReviewActivity.this, WriteReviewActivity.class);
                intent.putExtra("id", selectedReviewId);
                intent.putExtra("name", name);
                intent.putExtra("year", year);
                intent.putExtra("points", points);
                intent.putExtra("review", review);
                intent.putExtra("genre", genre);

                startActivity(intent);
            }

            cursor.close();
            db.close();

            return true;

        } else if (item.getItemId() == R.id.action_delete) {
            deleteReview(selectedReviewId);
            return true;

        } else if (item.getItemId() == R.id.action_view) {

            SQLiteDatabase db = dbHelper.getReadableDatabase();
            Cursor cursor = db.query(
                    "Reviews",
                    null,
                    "id=?",
                    new String[]{String.valueOf(selectedReviewId)},
                    null,
                    null,
                    null
            );
            if (cursor.moveToFirst()) {
                String name = cursor.getString(cursor.getColumnIndexOrThrow("name"));
                String year = cursor.getString(cursor.getColumnIndexOrThrow("year"));
                String points = cursor.getString(cursor.getColumnIndexOrThrow("points"));
                String review = cursor.getString(cursor.getColumnIndexOrThrow("review"));
                String genre = cursor.getString(cursor.getColumnIndexOrThrow("genre"));

                Intent intent = new Intent(ReadReviewActivity.this, MovieReviewPage.class);
                intent.putExtra("id", selectedReviewId);
                intent.putExtra("name", name);
                intent.putExtra("year", year);
                intent.putExtra("points", points);
                intent.putExtra("review", review);
                intent.putExtra("genre", genre);

                startActivity(intent);
            }

            cursor.close();
            db.close();

            return true;

        } else {
            return super.onContextItemSelected(item);
        }
    }

    private void deleteReview(int reviewId) {
        SQLiteDatabase db = dbHelper.getWritableDatabase();

        try {
            int rowsDeleted = db.delete("Reviews", "id=?", new String[]{String.valueOf(reviewId)});
            if (rowsDeleted > 0) {
                Toast.makeText(this, "Review deleted successfully!", Toast.LENGTH_SHORT).show();
                loadReviews(null, null, "id DESC");
            } else {
                Toast.makeText(this, "Error deleting review!", Toast.LENGTH_SHORT).show();
            }
        } catch (Exception e) {
            Toast.makeText(this, "Error: " + e.getMessage(), Toast.LENGTH_SHORT).show();
        } finally {
            db.close();
        }
    }
}
