package com.example.practice1;

import android.content.ContentValues;
import android.content.Context;
import android.content.Intent;
import android.database.sqlite.SQLiteDatabase;
import android.database.sqlite.SQLiteOpenHelper;
import android.os.Bundle;
import android.view.View;
import android.widget.AdapterView;
import android.widget.ArrayAdapter;
import android.widget.Button;
import android.widget.EditText;
import android.widget.Spinner;
import android.widget.Toast;

import androidx.activity.EdgeToEdge;
import androidx.appcompat.app.AppCompatActivity;
import androidx.core.graphics.Insets;
import androidx.core.view.ViewCompat;
import androidx.core.view.WindowInsetsCompat;



public class WriteReviewActivity extends AppCompatActivity {
    private TaskDatabaseHelper dbHelper;
    EditText movName, movTxt, yrTxt, ptsTxt;
    Spinner spinner;
    Button subBtn;

    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        EdgeToEdge.enable(this);
        setContentView(R.layout.activity_add_review);

        // Initialize database helper AFTER super.onCreate()
        dbHelper = new TaskDatabaseHelper(this);

        // Initialize views
        movName = findViewById(R.id.movTxt);
        movTxt = findViewById(R.id.writeRevTxt);
        yrTxt = findViewById(R.id.yrTxt);
        ptsTxt = findViewById(R.id.ptsTxt);
        spinner = findViewById(R.id.genreSpinner);
        subBtn = findViewById(R.id.subBtn);

        // Set up spinner data
        String[] data = {"Comedy", "Action", "Drama", "Romance"};
        ArrayAdapter<String> adapter = new ArrayAdapter<>(this, android.R.layout.simple_spinner_item, data);
        adapter.setDropDownViewResource(android.R.layout.simple_spinner_dropdown_item);
        spinner.setAdapter(adapter);

        // Check if this activity was launched for editing
        Intent intent = getIntent();
        if (intent.hasExtra("id")) {
            int id = intent.getIntExtra("id", -1); // Get ID for reference
            String name = intent.getStringExtra("name");
            String year = intent.getStringExtra("year");
            String points = intent.getStringExtra("points");
            String review = intent.getStringExtra("review");
            String genre = intent.getStringExtra("genre");

            // Populate fields with existing data for editing
            movName.setText(name);
            yrTxt.setText(year);
            ptsTxt.setText(points);
            movTxt.setText(review);

            // Set spinner selection based on genre
            int spinnerPosition = adapter.getPosition(genre);
            spinner.setSelection(spinnerPosition);

            subBtn.setOnClickListener(v -> {
                String updatedName = movName.getText().toString();
                String updatedYear = yrTxt.getText().toString();
                String updatedPoints = ptsTxt.getText().toString();
                String updatedReview = movTxt.getText().toString();
                String updatedGenre = spinner.getSelectedItem().toString();

                if (updatedName.isEmpty() || updatedYear.isEmpty() || updatedPoints.isEmpty() || updatedReview.isEmpty()) {
                    Toast.makeText(this, "Please fill all fields", Toast.LENGTH_SHORT).show();
                } else {
                    SQLiteDatabase db = dbHelper.getWritableDatabase();
                    ContentValues values = new ContentValues();
                    values.put("name", updatedName);
                    values.put("year", updatedYear);
                    values.put("points", updatedPoints);
                    values.put("review", updatedReview);
                    values.put("genre", updatedGenre);

                    int rowsUpdated = db.update(
                            "Reviews",
                            values,
                            "id=?",
                            new String[]{String.valueOf(id)}
                    );

                    if (rowsUpdated > 0) {
                        Toast.makeText(this, "Review updated successfully!", Toast.LENGTH_SHORT).show();
                        finish(); // Return to previous activity
                    } else {
                        Toast.makeText(this, "Error updating review!", Toast.LENGTH_SHORT).show();
                    }

                    db.close();
                }
            });
        } else {
            subBtn.setOnClickListener(v -> {
                // Handle submission logic for new reviews (existing code)
                String movNameTxt = movName.getText().toString();
                String yr = yrTxt.getText().toString();
                String points = ptsTxt.getText().toString();
                String review = movTxt.getText().toString();
                String genre = spinner.getSelectedItem().toString();

                if (movNameTxt.isEmpty() || yr.isEmpty() || points.isEmpty() || review.isEmpty())
                {
                    Toast.makeText(this, "Please fill all fields", Toast.LENGTH_SHORT).show();
                }
                else {
// Handle submission logic here (e.g., save to database)
                    SQLiteDatabase db = dbHelper.getWritableDatabase();
                    ContentValues values = new ContentValues();
                    values.put("name",movNameTxt);
                    values.put("year",yr);
                    values.put("points",points);
                    values.put("review",review);
                    values.put("genre",genre);

                    long result = db.insert("Reviews", null, values);
                    if (result != -1) {
                        Toast.makeText(this, "Review submitted successfully!", Toast.LENGTH_SHORT).show();

// Close the database before navigating
                        db.close();

// Use explicit intent with proper context
                        Intent intent1 = new Intent(WriteReviewActivity.this, ReadReviewActivity.class);
                        startActivity(intent1);

// Add this line to prevent returning to MainActivity
                        finish();
                    }
                    else
                    {
                        Toast.makeText(this,"Error saving task",Toast.LENGTH_SHORT).show();
                    }




                }
            });
        }
    }

}

