package com.example.moviereview

import android.content.Intent
import android.os.Bundle
import android.view.MenuInflater
import android.view.MenuItem
import android.view.View
import android.widget.*
import androidx.appcompat.app.AppCompatActivity

class MainActivity : AppCompatActivity() {
    lateinit var db: DBHelper
    lateinit var movieList: MutableList<String>
    lateinit var adapter: ArrayAdapter<String>
    lateinit var selectedItem: String
    lateinit var listView: ListView

    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)
        setContentView(R.layout.activity_main)

        db = DBHelper(this)

        val movieName = findViewById<EditText>(R.id.editMovie)
        val ratingBar = findViewById<RatingBar>(R.id.ratingBar)
        val btnAdd = findViewById<Button>(R.id.btnAdd)
        val btnShow = findViewById<Button>(R.id.btnShow)
        listView = findViewById(R.id.movieList)

        movieList = db.getAllMovies()
        adapter = ArrayAdapter(this, android.R.layout.simple_list_item_1, movieList)
        listView.adapter = adapter

        btnAdd.setOnClickListener {
            val name = movieName.text.toString()
            val rating = ratingBar.rating.toString()
            if (name.isNotEmpty()) {
                db.insertMovie("$name - $ratingâ­")
                refreshList()
                movieName.text.clear()
                ratingBar.rating = 0f
            } else {
                Toast.makeText(this, "Enter movie name", Toast.LENGTH_SHORT).show()
            }
        }

        btnShow.setOnClickListener {
            startActivity(Intent(this, GridActivity::class.java))
        }

        listView.setOnItemClickListener { _, view, position, _ ->
            selectedItem = movieList[position]
            showPopup(view)
        }
    }

    private fun showPopup(view: View) {
        val popup = PopupMenu(this, view)
        val inflater: MenuInflater = popup.menuInflater
        inflater.inflate(R.menu.popup_menu, popup.menu)

        popup.setOnMenuItemClickListener { item ->
            when (item.itemId) {
                R.id.menu_update -> {
                    db.updateMovie(selectedItem, "$selectedItem (Updated)")
                    refreshList()
                    true
                }
                R.id.menu_delete -> {
                    db.deleteMovie(selectedItem)
                    refreshList()
                    true
                }
                else -> false
            }
        }
        popup.show()
    }

    private fun refreshList() {
        movieList.clear()
        movieList.addAll(db.getAllMovies())
        adapter.notifyDataSetChanged()
    }
}

package com.example.moviereview

import android.content.ContentValues
import android.content.Context
import android.database.sqlite.SQLiteDatabase
import android.database.sqlite.SQLiteOpenHelper

class DBHelper(context: Context) : SQLiteOpenHelper(context, "MovieDB", null, 1) {
    override fun onCreate(db: SQLiteDatabase) {
        db.execSQL("CREATE TABLE movies(name TEXT PRIMARY KEY)")
    }

    override fun onUpgrade(db: SQLiteDatabase, oldVersion: Int, newVersion: Int) {
        db.execSQL("DROP TABLE IF EXISTS movies")
        onCreate(db)
    }

    fun insertMovie(name: String) {
        val db = writableDatabase
        val cv = ContentValues().apply {
            put("name", name)
        }
        db.insert("movies", null, cv)
    }

    fun getAllMovies(): MutableList<String> {
        val list = mutableListOf<String>()
        val db = readableDatabase
        val cursor = db.rawQuery("SELECT * FROM movies", null)
        while (cursor.moveToNext()) {
            list.add(cursor.getString(0))
        }
        cursor.close()
        return list
    }

    fun updateMovie(old: String, new: String) {
        val db = writableDatabase
        val cv = ContentValues().apply {
            put("name", new)
        }
        db.update("movies", cv, "name=?", arrayOf(old))
    }

    fun deleteMovie(name: String) {
        val db = writableDatabase
        db.delete("movies", "name=?", arrayOf(name))
    }
}
